import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { DataService, Message, Transcription } from '../../services/data.service';
import { Router } from '@angular/router';

@Component({
    selector: 'app-interview-results',
    standalone: true,
    imports: [CommonModule],
    template: `
        <div class="results-container" *ngIf="dataService.evaluationResults">
            <!-- Interview Session Section -->
            <div class="interview-session">
                <h1>AI Interview Session</h1>
                
                <!-- Transcription Display -->
                <div class="transcription-container">
                    <h3>Interview Transcription</h3>
                    <div class="chat-container">
                        <div *ngFor="let message of dataService.transcription?.conversation" 
                             class="message" 
                             [class.ai-message]="message.speaker === 'AI_HR'"
                             [class.candidate-message]="message.speaker === 'Candidate'">
                            <div class="message-header">
                                <i [class]="message.speaker === 'AI_HR' ? 'fas fa-robot' : 'fas fa-user'"></i>
                                <span>{{ message.speaker === 'AI_HR' ? 'AI Interviewer' : 'Candidate' }}</span>
                            </div>
                            <div class="message-content">
                                {{ message.text }}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <!-- Final Evaluation Section -->
            <h2>Final Evaluation</h2>
            <div class="evaluation-grid">
                <!-- Analysis Summary -->
                <div class="analysis-card">
                    <h3>Analysis Summary</h3>
                    
                    <div class="strengths">
                        <h4>
                            <span class="plus-icon">+</span>
                            Strengths
                        </h4>
                        <ul>
                            <li *ngFor="let strength of dataService.evaluationResults.strengths">
                                {{ strength }}
                            </li>
                        </ul>
                    </div>

                    <div class="improvements">
                        <h4>
                            <span class="arrow-icon">→</span>
                            Areas for Improvement
                        </h4>
                        <ul>
                            <li *ngFor="let area of dataService.evaluationResults.areasForImprovement">
                                {{ area }}
                            </li>
                        </ul>
                    </div>
                </div>

                <!-- Final Decision -->
                <div class="decision-card">
                    <h3>Final Decision</h3>
                    
                    <div class="decision-status" 
                         [class.accepted]="dataService.evaluationResults.finalDecision.decision === 'Accepted'"
                         [class.rejected]="dataService.evaluationResults.finalDecision.decision === 'Rejected'">
                        {{ dataService.evaluationResults.finalDecision.decision }}
                    </div>

                    <div class="ratings">
                        <div class="rating-item">
                            <div class="rating-label">Technical Skills</div>
                            <div class="rating-stars">
                                <span *ngFor="let star of [1,2,3,4,5]" 
                                      [class.filled]="star <= dataService.evaluationResults.finalDecision.ratings['Technical Skills']">
                                    ★
                                </span>
                            </div>
                        </div>

                        <div class="rating-item">
                            <div class="rating-label">Cultural Fit</div>
                            <div class="rating-stars">
                                <span *ngFor="let star of [1,2,3,4,5]" 
                                      [class.filled]="star <= dataService.evaluationResults.finalDecision.ratings['Communication Skills']">
                                    ★
                                </span>
                            </div>
                        </div>

                        <div class="rating-item">
                            <div class="rating-label">Experience Level</div>
                            <div class="rating-stars">
                                <span *ngFor="let star of [1,2,3,4,5]" 
                                      [class.filled]="star <= dataService.evaluationResults.finalDecision.ratings['Experience Level']">
                                    ★
                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <!-- Footer -->
            <div class="footer">
                <div class="logo">
                    <i class="fas fa-robot"></i>
                    HireAI
                </div>
                <div class="social-links">
                    <a href="#" target="_blank"><i class="fab fa-twitter"></i></a>
                    <a href="#" target="_blank"><i class="fab fa-linkedin"></i></a>
                    <a href="#" target="_blank"><i class="fab fa-github"></i></a>
                </div>
            </div>
        </div>
    `,
    styles: [`
        .results-container {
            max-width: 1200px;
            margin: 40px auto;
            padding: 20px;
            color: white;
            font-family: 'Inter', sans-serif;
        }

        h1, h2 {
            color: white;
            text-align: center;
            margin-bottom: 30px;
        }

        .interview-session {
            background: #151e33;
            border-radius: 16px;
            padding: 24px;
            margin-bottom: 40px;
        }

        .question-progress {
            display: flex;
            align-items: center;
            margin: 20px 0;
        }

        .progress-circle {
            width: 40px;
            height: 40px;
            background: #2563eb;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            font-weight: bold;
        }

        .progress-bar {
            flex: 1;
            height: 4px;
            background: #2563eb;
            margin: 0 15px;
        }

        .question-count {
            color: #94a3b8;
        }

        /* Transcription Styles */
        .transcription-container {
            margin-top: 30px;
            background: #1e293b;
            border-radius: 12px;
            padding: 20px;
        }

        .chat-container {
            max-height: 500px;
            overflow-y: auto;
            padding: 20px;
            display: flex;
            flex-direction: column;
            gap: 16px;
        }

        .message {
            max-width: 80%;
            padding: 12px;
            border-radius: 12px;
            position: relative;
        }

        .ai-message {
            background: #2563eb;
            align-self: flex-start;
            margin-right: auto;
        }

        .candidate-message {
            background: #374151;
            align-self: flex-end;
            margin-left: auto;
        }

        .message-header {
            display: flex;
            align-items: center;
            gap: 8px;
            margin-bottom: 8px;
            font-size: 0.9em;
            color: rgba(255, 255, 255, 0.8);
        }

        .message-content {
            line-height: 1.5;
        }

        .evaluation-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 24px;
            margin-top: 30px;
        }

        .analysis-card, .decision-card {
            background: #1e293b;
            border-radius: 16px;
            padding: 24px;
        }

        h3 {
            color: white;
            margin-bottom: 20px;
            font-size: 1.25rem;
        }

        h4 {
            display: flex;
            align-items: center;
            gap: 8px;
