import { Component, OnInit, OnDestroy } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { DataService } from '../../services/data.service'; // ✅ Import the service
import { interval, Subscription } from 'rxjs';
import { takeWhile } from 'rxjs/operators';
import { CommonModule } from '@angular/common';

@Component({
    selector: 'app-ai-interview',
    standalone: true,
    imports: [CommonModule],
    templateUrl: './ai-interview.component.html',
    styleUrl: './ai-interview.component.css'
})
export class AiInterviewComponent implements OnInit, OnDestroy {
    private statusCheckSubscription?: Subscription;
    public callStatus: string = 'initiating';
    public errorMessage: string = '';
    private maxRetries = 30; // 5 minutes maximum (10 second intervals)
    private retryCount = 0;

    constructor(private http: HttpClient, public dataService: DataService) { }

    ngOnInit(): void {
        this.monitorCallStatus();
    }

    ngOnDestroy(): void {
        if (this.statusCheckSubscription) {
            this.statusCheckSubscription.unsubscribe();
        }
    }

    public monitorCallStatus(): void {
        this.callStatus = 'initiating';
        this.errorMessage = '';
        this.retryCount = 0;
        
        this.statusCheckSubscription = interval(10000) // Check every 10 seconds
            .pipe(
                takeWhile(() => this.retryCount < this.maxRetries && this.callStatus === 'initiating')
            )
            .subscribe(() => {
                this.checkCallStatus();
            });
    }

    private checkCallStatus(): void {
        this.http.get('http://localhost:4000/api/calls/status').subscribe(
            (response: any) => {
                this.retryCount++;
                
                if (response.status === 'completed') {
                    this.callStatus = 'completed';
                    this.submitJobData();
                } else if (response.status === 'failed') {
                    this.callStatus = 'failed';
                    this.errorMessage = response.error || 'Call failed to connect';
                } else if (response.status === 'in_progress') {
                    this.callStatus = 'in_progress';
                }
            },
            (error) => {
                console.error('Error checking call status:', error);
                this.retryCount++;
                if (this.retryCount >= this.maxRetries) {
                    this.callStatus = 'failed';
                    this.errorMessage = 'Failed to check call status. Please try again.';
                }
            }
        );
    }

    private submitJobData(): void {
        this.http.get('http://localhost:4000/api/calls').subscribe(
            (response: any) => {
                console.log("✅ Server response:", response || []);
                if (response?.structured_conversation?.results?.channels?.[0]?.alternatives?.[0]?.transcript) {
                    this.dataService.job = response.structured_conversation.results.channels[0].alternatives[0].transcript;
                    this.finaleval(this.dataService.job, this.dataService.summary);
                } else {
                    this.errorMessage = 'No transcript available from the call';
                }
            },
            (error: any) => {
                console.error("❌ Error submitting job data:", error);
                this.errorMessage = 'Failed to process interview data';
            }
        );
    }

    private finaleval(conversation: string, summary: string): void {
        const conversationArray = conversation.split('\n');
        const jobData = {
            conversation: conversationArray,
            summary: summary
        };

        this.http.post('http://localhost:4000/api/finaleval', jobData).subscribe(
            (response: any) => {
                console.log("✅ Server response:", response);
            },
            (error: any) => {
                console.error("❌ Error submitting job data:", error);
                this.errorMessage = 'Failed to evaluate interview';
            }
        );
    }

    // fetchAudio(): void {
    //   fetch('http://localhost:3000/api/audio')
