    //     .then(blob => {
    //       const url = URL.createObjectURL(blob);
    //       const audio = document.getElementById('player') as HTMLAudioElement;
    //       audio.src = url;
    //     })
    //     .catch(error => {
    //       console.error("❌ Error fetching audio:", error);
    //     });
    // }
}
